package bluecrystal.chrome.sign;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertTrue;

import org.junit.Test;

public class PManagerTest {
	PkcsWrapper pcks = new PkcsWrapper("aetpkss1.dll;eTPKCS11.dll;asepkcs.dll;libaetpkss.dylib;libeTPkcs11.dylib",
			"/usr/local/lib");

	@Test
	public void testListCerts() throws Exception {
		if (!hasPIN())
			return;

		String json = NativeMessagingHost.listCerts(pcks);

		assertTrue(json.contains("\"alias\":\"RENATO DO AMARAL CRIVANO MACHADO:12031\""));
	}

	@Test
	public void testGetCertificate() throws Exception {
		if (!hasPIN())
			return;

		String json = NativeMessagingHost.listCerts(pcks);
		String cert = pcks.getCert("RENATO DO AMARAL CRIVANO MACHADO:12031");
		assertTrue(cert.startsWith("MII"));
	}

	@Test
	public void testGetSubject() throws Exception {
		if (!hasPIN())
			return;

		String json = NativeMessagingHost.listCerts(pcks);
		String subject = pcks.getSubject("RENATO DO AMARAL CRIVANO MACHADO:12031");
		assertTrue(subject.startsWith("CN="));
	}

	@Test
	public void testGetKeySize() throws Exception {
		if (!hasPIN())
			return;

		String json = NativeMessagingHost.listCerts(pcks);
		int keySize = pcks.getKeySize("RENATO DO AMARAL CRIVANO MACHADO:12031");
		assertEquals(2048, keySize);
	}

	@Test
	public void testSign() throws Exception {
		if (!hasPIN())
			return;

		String hashADRB21 = "";
		String signatureADRB21 = "";

		String json = NativeMessagingHost.listCerts(pcks);
		NativeMessagingHost.current.alias = "RENATO DO AMARAL CRIVANO MACHADO:12031";
		NativeMessagingHost.current.userPIN = getPIN();
		String signature = NativeMessagingHost.pcksSign(pcks, 2, hashADRB21);
		assertEquals(signatureADRB21, signature.replace("\n", "").replace("\r", ""));
	}

	private String getPIN() {
		return System.getProperty("TOKEN_PASSWORD");
	}

	private boolean hasPIN() {
		if (getPIN() == null) {
			System.out.println("No TOKEN_PASSWORD property available, skipping test.");
			return false;
		}
		return true;
	}

}
